package week1.day1.assignments;

import java.util.*;

public class ConversionOfNegativeToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num =-40; // Initialzing the value into num
		int positiveNumber;
	
		if(num < 0) {
			 positiveNumber = num *(-1);
			System.out.println("Positive number is :" + positiveNumber);
		}
		

	}

}
